mapper.select:
SELECT ad_no, company_id, ad_group_cd, ad_content, ad_img, ad_apply_date, approve_state_nm, payment_finish_date FROM tb_ad JOIN tb_payment JOIN tb_approve_state ON payment_target_no = ad_no WHERE ad_approve_state_cd = 1 AND ad_approve_state_cd = approve_state_cd
AND member_id = #{memberId}
ORDER BY ad_no DESC LIMIT #{beginRow}, #{pagePerRow}

SELECT admin_id, member_id, payment_target_no, payment_price, payment_total_price, payment_apply_date, payment_finish_date, payment_state_nm FROM tb_payment a JOIN tb_payment_state b ON a.payment_state_cd = b.payment_state_cd WHERE a.payment_state_cd > 2
AND member_id = #{memberId}
ORDER BY payment_target_no DESC LIMIT #{beginRow}, #{pagePerRow}

SELECT ad_no, company_id, ad_content, ad_img, ad_reg_date, ad_end_date, admin_id FROM tb_ad WHERE ad_approve_state_cd=2
AND company_id = #{memberId}
ORDER BY ad_no DESC LIMIT #{beginRow}, #{pagePerRow}

mapper.update:
UPDATE
	tb_ad
SET
	ad_img = #{adImg}
WHERE 
	ad_no = #{adNo}
