mapper.insert:
INSERT INTO tb_person(
	person_id,
	person_birth,
	person_gender
	)
VALUE(
	#{memberId},
	#{personBirth},
	#{personGender}
	)

mapper.select:
SELECT m.member_id, m.member_nm, m.member_phone, m.member_email, p.person_birth, p.person_gender, a.act_state_nm FROM tb_member m JOIN tb_person p ON m.member_id = p.person_id JOIN tb_act_state a ON m.act_state_cd = a.act_state_cd
WHERE ${selectOption} LIKE '%${selectValue}%'
ORDER BY m.member_reg_date DESC LIMIT #{beginRow}, #{pagePerRow}
