mapper.insert:
INSERT INTO tb_post( member_id, post_title, post_content, post_reg_date
,post_img
) VALUES( #{memberId}, #{postTitle}, #{postContent}, now()
,#{postImg}
)

mapper.update:
UPDATE tb_post SET post_title = #{postTitle}, post_content = #{postContent}, post_modify_date = now()
,post_img = #{postImg}
WHERE post_no = #{postNo}
