mapper.select:
SELECT COUNT(*) FROM tb_reserve rsv JOIN tb_room r JOIN tb_payment_state pmst JOIN tb_company cp JOIN tb_member perm JOIN tb_payment pm JOIN tb_member comm ON rsv.room_no = r.room_no AND rsv.payment_state_cd = pmst.payment_state_cd AND r.company_id = cp.company_id AND rsv.person_id = perm.member_id AND rsv.reserve_no = pm.payment_target_no AND comm.member_id = r.company_id
WHERE rsv.person_id = #{login.memberId}
WHERE cp.company_id = #{login.memberId}
