mapper.select:
SELECT t.trip_no, t.person_id, t.trip_theme_cd, tn.trip_theme_nm, t.region_cd, r.region_do, r.region_si, t.recruit_state_cd, rn.recruit_state_nm, t.trip_title, t.trip_content, t.trip_present_member, t.trip_max_member, t.trip_tag, t.trip_start_date, t.trip_end_date, t.trip_per_price, t.trip_total_price, t.trip_recruit_start_date, t.trip_recruit_end_date, t.trip_reg_date, t.trip_modify_date, t.trip_delete_date FROM tb_trip t JOIN tb_trip_theme tn ON t.trip_theme_cd = tn.trip_theme_cd JOIN tb_region r ON t.region_cd = r.region_cd JOIN tb_recruit_state rn ON t.recruit_state_cd = rn.recruit_state_cd WHERE t.trip_delete_date is null
AND t.person_id = #{memberId}
AND t.person_id = #{memberId}
AND t.trip_no =#{tripNo}
ORDER BY trip_reg_date DESC LIMIT 0, #{lastTripRow}

mapper.update:
UPDATE 
	tb_trip
SET
	trip_total_price = (trip_total_price

						+ ${planPrice}


						- ${planLastPrice}

						),
	trip_per_price = Round(trip_total_price/trip_max_member)
WHERE	
	trip_no = #{tripNo}

mapper.delete:
DELETE FROM 
	tb_plan
WHERE

	plan_no = #{planNo}


	trip_no = #{tripNo}

mapper.update:
UPDATE tb_group SET approve_state_cd = #{approveStateCd}
,group_approve_date = now()
,group_drop_date = now()
WHERE trip_no = #{tripNo} AND approve_state_cd = 1
AND person_id = #{personId}
AND group_no != #{groupNo}

UPDATE tb_trip SET recruit_state_cd = #{recruitStateCd}
,trip_recruit_end_date = now()
,trip_present_member = trip_present_member + 1
,trip_present_member = trip_present_member - 1
WHERE trip_no = #{tripNo}

mapper.delete:
DELETE FROM
	tb_group
WHERE
	trip_no = #{tripNo}
AND
	person_id=#{personId}
